let FocusCosts = (start:datetime, end:datetime) 
{ 
    Costs()
    | where ChargePeriodStart >= start and ChargePeriodStart < end
    | project todecimal(EffectiveCost), TagBag=parse_json(Tags)
    | mv-expand tag = bag_keys(TagBag)
    | where iif(tostring(selectedTag) <> "", tag == tostring(selectedTag), tag <> tostring(selectedTag))
    | extend tagValue = tostring(TagBag[tostring(tag)])
    | where iif(tostring(selectedValue) <> "", tagValue == tostring(selectedValue), tagValue <> "")
    | summarize Cost = round(sum(EffectiveCost), 2) by TagName=tostring(tag), TagValue=tagValue, TagString=strcat(tag, " : ", tagValue)
    | order by Cost desc 
    | top 100 by Cost desc
};
FocusCosts(startofmonth(now()), endofmonth(now()));

